export function validateModeInputs(){Array.from(document.querySelectorAll("input[type='number'].form-control")).forEach(a=>{a.addEventListener("keydown",t=>{let l=t.key;if(!validInputs.includes(l))switch(a.value.length){case 0:try{if("Backspace"===l||"Tab"===l)break;if(possible_decimals.includes(l)){a.value="0.5";break}if(e(parseFloat(l)))break}catch{t.preventDefault();break}case 1:if("Backspace"===l||"Tab"===l)break;if(possible_decimals.includes(l)&&"4"!==a.value){t.preventDefault(),a.value+=".5";break}t.preventDefault();break;case 3:if("Tab"===l)break;if("Backspace"!==l){t.preventDefault();break}t.preventDefault(),a.value=a.value[0]}})});const e=e=>e>=0&&e<=4&&e%.5==0}const validInputs=["Control","ArrowLeft","ArrowRight","Shift","Delete"],possible_decimals=[".",",","Unidentified"," "];